<template>	<div id="secondcomponent">		<h1>I am another page</h1>		<p>written by {{ author }}</p>		<p>感谢 <a href="#">ytcheng</a>的技术指导</p>		<ul>			<li v-for="article in articles">				{{ article.title }} - 收藏:{{ article.collect_count }}			</li>		</ul>	</div></template><script>	export default {		data(){			return {				author : 'ytcheng',				articles: []			}		},		mounted: function(){			this.$http.jsonp('https://api.douban.com/v2/movie/top250?count=10',{},{				headers: {				},				emulateJSON: true			}).then(function(response){				// 这里是处理正确的回调				this.articles = response.data.subjects				// this.articles = response.data["subjects"] 也可以			},function(response){				// 这里是处理错误的回调				console.log(response);			});		}	}</script>